sap.ui.define(["sap/ui/core/mvc/Controller", 'sap/m/MessageToast'], function(Controller, MessageToast) {
	"use strict";
	return Controller.extend("ztestappszDebugMe.controller.DebugMe", {
		/**
		 *@memberOf ztestappszDebugMe.controller.DebugMe
		 */
		cmdAdd: function() {
			//This code was generated by the layout editor.
			var first = parseInt(this.getView().byId("input1").getValue());
			var second = parseInt(this.getView().byId("input2").getValue());
			var sum = first + second;
			MessageToast.show("Sum is " + sum);
		}
	});
});